import React from 'react'

const Avatar = ({ userId, username }) => {

    const colors = ['bg-red-200', 'bg-purple-200',
        'bg-green-200', 'bg-blue-200',
        'bg-yellow-200', 'bg-teal-200'];


    return (
        <div className='flex border-b border-gray-100 py-2 items-center'>
            <div className={` bg-[#${userId.subString(8)}}] font-bold text-white text-center rounded-full h-10 w-10 m-2 p-2`}>
                {username[0].toUpperCase()}
            </div>
            <div >
                <span>{username}</span>
            </div>
        </div>
    )
}

export default Avatar
