import { useContext, useState } from "react"
import axios from "../utils/axios";
import requests from "../utils/requests";
import { UserContext } from "../context/UserContext";

const Register = () => {

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [isLoginOrRegister , setIsLoginOrRegister] = useState(false);

    const {setUsername: setLoggedInUsername, setId} = useContext(UserContext);

    const register = async (event) =>{
        event.preventDefault();
        const {data} = await axios.post(requests.registerUser, {username,password});
        setLoggedInUsername(username);
        setId(data.userId);
    }

    return (
        <div className="bg-blue-50 h-screen flex items-center">
            <form className="w-64 mx-auto mb-12" onSubmit={register}>
                <input
                    value={username}
                    onChange={e => setUsername(e.target.value)}
                    type="text"
                    placeholder="username"
                    className="block w-full rounded-sm p-2 mb-2" />

                <input
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                    type="password"
                    placeholder="password"
                    className="block w-full rounded-sm p-2 mb-2" />

                <button className="bg-blue-500 text-white block p-2 mb-2 w-full rounded-sm">{isLoginOrRegister ? 'Log In':'Register'}</button>
                <div className="text-center mt-2">
                    Already have account?
                    <button onClick={(e)=>{e.preventDefault();setIsLoginOrRegister(true)}}> Login here</button>
                </div>
            </form>
        </div>
    )
}

export default Register
